<style lang="scss">
.MargTitle {
    position: relative;
    display: inline-block;

    &__name {
        font-family: "Macabre";
        text-transform: uppercase;
        letter-spacing: 0.055rem;
        font-size: 1.75rem;
        line-height: 1;
    }

    &__title {
        font-family: "Vulf Mono", serif;
        line-height: 1;
        font-size: 0.95em;
        padding-top: 0.5rem;
    }

    div,
    svg {
        transition: all 150ms linear;
    }

    &.size {
        &-m {

            .green-name,
            .black-name {
                svg {
                    max-width: 260px;
                }
            }

            .title {
                svg {
                    max-width: 360px;
                }
            }
        }

        &-s {

            .green-name,
            .black-name {
                svg {
                    max-width: 200px;
                }

                width: fit-content;
            }

            .black-name {
                display: flex;
            }

            .title {
                svg {
                    max-width: 300px;
                }
            }
        }

        &-xs {

            .green-name,
            .black-name {
                svg {
                    max-width: 150px;
                    height: 30px;
                }

                width: fit-content;
            }

            .black-name {
                display: flex;
            }

            .title {
                svg {
                    max-width: 270px;
                    transform: translateY(0.25em);
                }

                &.title-abridged {
                    // transform: translateX(-3em);
                    // opacity: 0.5;
                    //display: none;
                }
            }
        }
    }

    &.info-abridged {
        //display: flex;
        align-items: baseline;

        .names {
            transform: translateY(3px);
        }
    }


    .green-name {
        position: absolute;
        top: 0.15em;
        left: 0;
    }

    .black-name {
        position: relative;

        path {
            fill: var(--name-title-background);
        }

        // &:hover {
        //     path {
        //         fill: var(--name-title-background--hover)
        //     }
        // }

        g {
            transform: rotate(-25, 10, 10);
        }

        //opacity: 0;
        // top: 0;
        // left: 0;
    }

    .title {
        path {
            fill: var(--text-base-color);
        }
    }
}
</style>
